create database if not exists TestFlow;use TestFlow;create table if not exists apps (  id int not null auto_increment,  label varchar(50),  class varchar(50),  PRIMARY KEY (id)) ENGINE = InnoDB;create table if not exists suites (  id int not null auto_increment,  label varchar(100),  description varchar(500),  PRIMARY KEY (id)) ENGINE = InnoDB;insert into suites(label, description) values('Default', '');create table if not exists tests (  id int not null auto_increment,  baseClass varchar(100),  label varchar(100),  description varchar(500),  appId int default 1,  suiteId int default 1,  PRIMARY KEY (id),  FOREIGN KEY (appId)  REFERENCES apps(id)  ON DELETE SET NULL,  FOREIGN KEY (suiteId)  REFERENCES suites(id)  ON DELETE SET NULL) ENGINE = InnoDB;delimiter //create procedure insertTest(   baseClass varchar(100),  label varchar(100),  description varchar(500),  appId int,  suiteId int) BEGIN  set @suite = suiteId;  if @suite = -1 then     set @suite = null;   end if;    insert into tests (baseClass, label, description, appId, suiteId)  values (baseClass, label, description, appId, @suite);END//delimiter ;create table if not exists testRuns (  id int not null auto_increment,  testId int not null,  passed bool not null,  timeElapsed int not null,  steps text,  runTime datetime default now(),  PRIMARY KEY (id)) ENGINE = InnoDB;