create database if not exists TestFlow;use TestFlow;create table if not exists suites (  id int not null auto_increment,  label varchar(100),  description varchar(500),  PRIMARY KEY (id)) ENGINE = InnoDB;create table if not exists tests (  id int not null auto_increment,  baseClass varchar(100),  label varchar(100),  description varchar(500),  suiteId int default 1,  PRIMARY KEY (id),  FOREIGN KEY (suiteId)  REFERENCES suites(id)  ON DELETE SET NULL  ) ENGINE = InnoDB;insert into suites(label, description) values('Default', '');  